public TileMap(Context context, Paint paint) {
this.paint = paint;
this.context = context;
mapAtlas = new TextureAtlas(context, sprite_sheet);
tileMap = new HashMap<TileType, Bitmap>();

tileMap.put(TileType.WALL, mapAtlas.cut(Texture.SPRITE_SIZE * 0, Texture.SPRITE_SIZE * 1,
        Texture.SPRITE_SIZE, Texture.SPRITE_SIZE, Texture.MULT_SIZE, false));
tileMap.put(TileType.GRASS1, mapAtlas.cut(Texture.SPRITE_SIZE * 1, Texture.SPRITE_SIZE * 0,
        Texture.SPRITE_SIZE, Texture.SPRITE_SIZE, Texture.MULT_SIZE, false));
    tileMap.put(TileType.TREE, mapAtlas.cut(Texture.SPRITE_SIZE * 15, Texture.SPRITE_SIZE * 2,
            Texture.SPRITE_SIZE, Texture.SPRITE_SIZE, Texture.MULT_SIZE, false));
    tileMap.put(TileType.DOOR, mapAtlas.cut(Texture.SPRITE_SIZE * 6, Texture.SPRITE_SIZE * 0,
            Texture.SPRITE_SIZE, Texture.SPRITE_SIZE, Texture.MULT_SIZE, false));
    tileMap.put(TileType.FLOOR, mapAtlas.cut(Texture.SPRITE_SIZE * 14, Texture.SPRITE_SIZE * 0,
            Texture.SPRITE_SIZE, Texture.SPRITE_SIZE, Texture.MULT_SIZE, false));
}

public TileMap() {
map_pos_x = new float[tiles_amount_x][tiles_amount_y];
map_pos_y = new float[tiles_amount_x][tiles_amount_y];

fillMap();
}

public void render(Canvas canvas, Hero hero) {
for (int i = (int) hero.getY() / tile_size - radiusY + 2; i < hero.getY() / tile_size + radiusY; i++){
for (int j = (int) hero.getX() / tile_size - radiusX + 2; j < hero.getX() / tile_size + radiusX; j++){
//    if(Physic.pDistanceXorY(tile_size * j, hero.getX()) < (radiusX + 2) * tile_size &&
//            Physic.pDistanceXorY(tile_size * i, hero.getY()) < (radiusY + 3) * tile_size) {
//        canvas.drawBitmap(tileMap.get(TileType.getTile(map[i][j])), tile_size * j, tile_size * i, paint);
//    }
    canvas.drawBitmap(tileMap.get(TileType.getTile(map[i][j])), tile_size * j, tile_size * i, paint);
}
}
}

public void resize(){
    tile_size = Texture.TOTAL_SIZE;
    size_x = tiles_amount_x * tile_size;
    size_y = tiles_amount_y * tile_size;

    radiusX = (int) GameActivity.screenSizeX / 2 / tile_size;
    radiusY = (int) GameActivity.screenSizeY / 2 / tile_size;
}


private void fillMap() {
for (int i = 0; i < tiles_amount_y; i++){
for (int j = 0; j < tiles_amount_x; j++){
map_pos_x[i][j] = (float) (tile_size * j);
map_pos_y[i][j] = (float) (tile_size * i);

if(map[i][j] == 2 || map[i][j] == 7){
map_walls_x.add(map_pos_x[i][j]);
map_walls_y.add(map_pos_y[i][j]);


}

}
}
}

